use std::fmt::Debug;

use bytemuck::{Pod, Zeroable};
use itertools::izip;
use rand::distributions::{Distribution, Standard};
use rand::Rng;
use serde::{Deserialize, Serialize};

pub use crate::bits::Bits;

#[repr(C)]
#[derive(
    Clone,
    Copy,
    PartialEq,
    Eq,
    PartialOrd,
    Ord,
    Debug,
    Default,
    Serialize,
    Deserialize,
    Pod,
    Zeroable,
)]
pub struct Template {
    pub code: Bits,
    pub mask: Bits,
}

impl Template {
    pub fn rotations(&self) -> impl Iterator<Item = Template> + '_ {
        let codes = self.code.rotations();
        let masks = self.mask.rotations();
        codes
            .into_iter()
            .zip(masks)
            .map(|(code, mask)| Template { code, mask })
    }

    pub fn distance(&self, other: &Self) -> f64 {
        self.rotations()
            .map(|r| r.fraction_hamming(other))
            .fold(f64::INFINITY, |a, b| a.min(b))
    }

    pub fn fraction_hamming(&self, other: &Self) -> f64 {
        let mut num = 0;
        let mut den = 0;

        for (ap, am, bp, bm) in izip!(
            self.code.0.iter(),
            self.mask.0.iter(),
            other.code.0.iter(),
            other.mask.0.iter()
        ) {
            let m = am & bm;
            let p = (ap ^ bp) & m;
            num += p.count_ones();
            den += m.count_ones();
        }

        // tmp_dist
        (num as f64) / (den as f64)
    }
}

impl Distribution<Template> for Standard {
    fn sample<R: Rng + ?Sized>(&self, rng: &mut R) -> Template {
        Template {
            code: rng.gen(),
            mask: rng.gen(),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    const A: &str = indoc::indoc! { r#"{
        "code": "",
        "mask":""
    }"# };

    const B: &str = indoc::indoc! { r#"{
        "code": "",
        "mask":""
    }"# };

    #[test]
    fn hamming_distance_with_rotations() {
        let a: Template = serde_json::from_str(A).unwrap();
        let b: Template = serde_json::from_str(B).unwrap();

        let d = a.distance(&b);

        assert_eq!(d, 0.35594620103822555); // TODO comparing wrong value!! Should be 0.3352968352968353 with correct rotations, update when rotations are fixed
    }

    #[test]
    fn hamming_distance_no_rotations() {
        let a: Template = serde_json::from_str(A).unwrap();
        let b: Template = serde_json::from_str(B).unwrap();

        let d = a.fraction_hamming(&b);

        assert_eq!(d, 0.47833602212491355);
    }
}
